import React, { createContext, useState } from 'react'

export const AsignContext = createContext({});
const useReducer=(state,{type,payload})=>{
    switch (type) {
        case 'insert':
            return state.push({
                productId: state.length+1, 
                price: Math.randome()
            })
            break;
    
        default:
            break;
    }
}

function AsignContextProvider({ children }) {
    const [cards, dispatch] = useReducer(reducers,[]) 
    const [name, setName] = useState({
        first_name: null,
        last_name: null

    });

    return (
        <AsignContext.Provider value={{cards, dispatch }}>
            {children}
        </AsignContext.Provider>
    )
}

export default AsignContextProvider
