import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react'
import { Link, useParams } from 'react-router-dom'



function ProductDetails() {

    const [data, setData] = useState([]);
    const param = useParams();

    useEffect(() => {
        getAllProduct();
    }, [])

    const getAllProduct = () => {
        fetch(`http://localhost:5000/api/products/get/${param.id}`, {
            method: "GET",
            headers: {
                authorization: 'Bearer ' + window.localStorage.getItem('token')
            },
        })
            .then(res => res.json())
            .then(res => {
                setData(res);
            })
    }

    return (

        <div className="card">
            <div className="card-header d-flex flex-wrap align-items-center justify-content-between ">
                <h4 className='m-0'>Products Details</h4>
                <div>
                    <Link to="/dashboard/all-products"
                        className='btn btn-sm btn-info rounded'>
                        <i className='fas fa-arrow-left'></i> Back
                    </Link>
                </div>
            </div>
            <div className="card-body">
                <div className="row">
                    <div className="col-md-6">
                        <div>
                            <img className='img-fluid' src={'http://localhost:5000/' + data.thumb_image} alt="" />

                        </div>
                        <ul className='d-flex my-2' style={{gap:10}}>
                            {
                                data.related_image?.map((image, index) => {
                                    return (
                                        <li key={index}>
                                            <img src={'http://localhost:5000/' + image} style={{ width: 50 }} />
                                        </li>
                                    )
                                })
                            }
                        </ul>
                    </div>
                    <div className="col-md-6">
                        <table>
                            <tbody>
                                <tr>
                                    <td style={{ width: 100 }}>Title</td>
                                    <td style={{ width: 3 }}>:</td>
                                    <td>{data.title}</td>
                                </tr>
                                <tr>
                                    <td>Category</td>
                                    <td>:</td>
                                    <td>{data.category.title}</td>
                                </tr>
                                <tr>
                                    <td>Price</td>
                                    <td>:</td>
                                    <td>{data.price}</td>
                                </tr>
                                <tr>
                                    <td>Discount</td>
                                    <td>:</td>
                                    <td>{data.price}</td>
                                </tr>
                                
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProductDetails
