import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'

function EditCategory() {

    const [data, setData] = useState([]);
    const [category, setCategory] = useState({});
    const {id, title} = useParams();

    useEffect(() => {
        getCategory();
        getCategoryById();
    }, [])
    const getCategoryById = () => {
        fetch('http://localhost:5000/api/category/get/'+id, {
            method: "GET",
            headers: {
                authorization: 'Bearer ' + window.localStorage.getItem('token')
            },
        })
            .then(res => res.json())
            .then(res => {
               setCategory(res);
            })
    }
    const getCategory = () => {
        fetch('http://localhost:5000/api/category/all', {
            method: "GET",
            headers: {
                authorization: 'Bearer ' + window.localStorage.getItem('token')
            },
        })
            .then(res => res.json())
            .then(res => {
                console.log(res);
                setData(res);
            })
    }


    const submitHandler = (e) => {
        e.preventDefault();

        let formData = new FormData(e.target);

        fetch('http://localhost:5000/api/category/edit', {
            method: "POST",
            headers: {
                authorization: 'Bearer ' + window.localStorage.getItem('token')
            },
            body: formData,
        })
            .then(res => res.json())
            .then(res => {
                console.log(res);
                window.alert('category successfuly created');
                e.target.reset();
            })
    }
    
    return (
        <div className="card">
            <div className="card-header d-flex flex-wrap align-items-center justify-content-between ">
                <h4 className='m-0'>Edit Category</h4>
                <div>
                    <Link to="/dashboard/categories"
                        className='btn btn-sm btn-info'>
                        <i className='fas fa-arrow-left'></i> Back
                    </Link>
                </div>
            </div>
            <div className="card-body">
                <form action="" onSubmit={submitHandler}>
                    <div className="form-group mb-3">
                        <label htmlFor="">Title</label>
                        <input type="text" defaultValue={category.value} className='form-control' name='title' />
                    </div>
                    <div className="form-group mb-3">
                        <label htmlFor="">Parent</label>
                        <ul>
                            <li>
                                <label htmlFor="none">
                                    <input defaultChecked value="none" id='none' name='parent' type="radio" />
                                    &nbsp;none
                                </label>
                            </li>
                            {
                                data.map(i => {
                                    return (
                                        <li key={i._id}>
                                            <label htmlFor={i._id}>
                                                
                                                <input
                                                defaultChecked={i.id === category.parent} 
                                                value={i._id} 
                                                id={i._id} 
                                                name='parent' type="radio" />
                                                &nbsp;{i.title}
                                            </label>
                                        </li>
                                    )
                                })
                            }

                        </ul>
                    </div>
                    <button className='btn btn-secondary'>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default EditCategory
