import React, { useContext, useReducer } from 'react'
import { Outlet } from 'react-router-dom'
import Footer from '../frontend/shared/Footer'
import Header from '../frontend/shared/Header'
import { AsignContext } from '../../context/AsignContext'
// import { listIndexes } from '../../../../baby-shop-server-site/models/modelUser'



function FrontendLayout() { 
    
    const {state, dispatch } = useContext(AsignContext);
    const {cards,showAlert} = state;
    console.log(state);
    return (
        <>
            <div id='index'>
                {/* <h1>{name.first_name}</h1>
                <h1>{name.last_name}</h1>
                <input type="text" onKeyUp={(e)=>setName({...name,first_name:e.target.value})} placeholder='first_name' />
                <input type="text" onKeyUp={(e)=>setName({...name,last_name:e.target.value})} placeholder='last_name' /> */}
                <button onClick={()=>dispatch({type:'insertCard', payload: null})}>add to card</button>
               
                <ul>
                    {
                        cards.map((item,index)=>{
                            return(
                                <li key={index}>
                                    <h1>{item.productId} : {parseInt(item.price*1000)}</h1>
                                    <button onClick={()=>dispatch({type:'deleteCard', payload: {index}})}>delete</button>
                                    
                                </li>
                               
                            )
                            
                        })
                        
                    }
                </ul>
                <button onClick={()=>dispatch({type:'toggleAlert', payload:null })}>toggle</button>
                
                
                <main>
                    <div id='menu_wrapper'>
                        {/* ---------------HEADER OPTION ---------- */}
                        <Header />
                        {/* --------------------home page------------ */}
                        <Outlet />
                        {/* -----------------footer-------------------- */}
                        <Footer />

                    </div>
                </main>
            </div>
        </>
    )
}

export default FrontendLayout
