import React, { useContext } from 'react'
import { AuthContext } from '../../../context/AuthContext';
import useFrontendContext from '../../../hooks/useFrontendContext';

function WishList() {
    const { checkAuth } = useContext(AuthContext);
    const { state, dispatch } = useFrontendContext();
  
    let cards = [];

    if (state?.cards) {
        cards = state.cards;
    }
    const { totla_cards_amount } = state;
    return (
        <>
            <table border='1' className='table table-responsive'>
                <thead>
                    <th>
                        <td>ID</td>
                        <td>Name</td>
                        <td>price</td>
                        <td>discount</td>
                        <td>Product Image</td>
                        <td>valid date</td>
                    </th>
                </thead>
                <tbody>
                {
                     cards.map((product,index)=>{
                               return (
                                <td key={index}>
                                    <tr>{product._id}</tr>
                                    <tr>{product.name}</tr>
                                    <tr>{product.price}</tr>
                                    <tr>{product.discount}</tr>
                                </td>
                               )
                            })
                            
                }
                </tbody>

            </table>
        </>
    )
}

export default WishList
