import React, { createContext, useReducer, useState } from 'react'

const fakeApi=()=>{
    return new Promise((resolve,reject)=>{
        setTimeout(() => {
            resolve('ok')
        }, 2000);
    })
}
const loadAsncData = ()=>{
    fakeApi()
    .then(res=>{
        
    })
}
const reducers=(state,{type,payload})=>{
    let tempState = {...state};
    let {cards,wishList,showAlert,showModel}=tempState;
    switch (type) {
        case 'insertCard':  
            cards.push({
                productId: cards.length+1, 
                price: Math.random()
            })
            return tempState;
            case 'deleteCard':
                cards.splice(payload.index,1)
                return tempState;
            case 'toggleAlert':
                tempState.showAlert= ! showAlert;
                return tempState;
        default:
            return state;
    }
}
export const AsignContext = createContext({});
function AsignContextProvider({ children }) {
    const [state, dispatch] = useReducer(reducers,{
        cards: [],
        wishList: [],
        showAlert: false,
        showModel: false,
    })

    const thunkDispatch = ({fn,type,payload})=>{
    if(fn=='async'){
            console.log("async calls")
        }else{
            dispatch({type,payload});
        }
     } 

    return (
        <AsignContext.Provider value={{state, dispatch: thunkDispatch }}>
            {children}
        </AsignContext.Provider>
    )
}

export default AsignContextProvider
